package cbit.gui.graph;
import java.awt.*;
/**
 * This class was generated by a SmartGuide.
 * 
 */
public class GraphLayout implements GraphLayoutManager {
/**
 * layout method comment.
 */
public void layout(GraphModel graphModel, GraphPane graphPane) throws Exception {
	Graphics2D g = (Graphics2D)graphPane.getGraphics();
	Shape topShape = graphModel.getTopShape();
	//
	// compute nominal sizes and positions of children
	//
	if (topShape!=null){
		topShape.change_managed(g);
		graphModel.resize(g,graphPane.getSize());
	}		
}


/**
 * layout method comment.
 */
public void layout(GraphModel graphModel, Graphics2D g2d, Dimension size) throws Exception {
	Shape topShape = graphModel.getTopShape();
	//
	// compute nominal sizes and positions of children
	//
	if (topShape!=null){
		topShape.change_managed(g2d);
		graphModel.resize(g2d,size);
	}		
}
}
